    my_vlogstatusid,
    my_vlogmessage,
    my_vlogobjectname,
    my_vparameters
);

CREATE FUNCTION createlog(vlogstatusid integer, vlogmessage character varying, vlogobjectname character varying, vparameters character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$
      DECLARE vLogID integer:= (select nextval('LogID'));
	      vSqlCmd varchar := 'INSERT INTO LogTable(LogID, LogStatusID, LogMessage, LogObjectName, Parameters,CreatedAt)'
				 || 'VALUES( '
				 || cast(vLogID as varchar)
				 || ', ' || cast(vLogStatusID as varchar)
				 || ', ''' || vLogMessage
				 || ''', ''' || vLogObjectName
				 || ''', ''' || vParameters
				 || ''', ' || 'now())';
		 vConnName text := 'conn';
		 vConnString text := 'dbname=oauthdb port=5432 host=localhost user=oauthdb_loguser password=sWXxBK76SVtWLUcAuQkwx9WNSKTDGZQ6oHJVSQXFwhRsCXhFXEW5WvdUvottdcWx';
	      vConnExist bool := (select ('{' || vConnName || '}')::text[] <@ (select dblink_get_connections()));
      BEGIN

		if(not vConnExist or vConnExist is null) then
				perform dblink_connect(vConnName,vConnString);
		end if;
				perform dblink(vConnName,vSqlCmd);
				perform dblink_disconnect(vConnName);
				set role postgres;
      END;
  $$;

