CREATE FUNCTION createscope(visdefault boolean, vparameters character varying, vdescription character varying) RETURNS TABLE(scopeuuid uuid, isdefault boolean, parameters character varying, description character varying, createdat timestamp with time zone) LANGUAGE plpgsql AS $$ #variable_conflict use_column DECLARE vScopeID integer := (select nextval('ScopeID')); vScopeUUID uuid := (select uuid_generate_v4()); BEGIN INSERT INTO scopes (scopeid,scopeuuid,isdefault,parameters,description,createdat) VALUES(vScopeID,vScopeUUID,visdefault,vparameters,vdescription,now());
-- Begin Log if success perform public.createlog(0,'Created Scope sucessfully', 'createscope', 'IsDefault: ' || cast(vIsDefault as varchar) || ', vParameters: ' || vparameters || ', Description: ' || vdescription);
-- RETURN RETURN QUERY (select ScopeUUID, IsDefault, Parameters, Description, createdat at time zone 'utc' from scopes where scopeid = vScopeID);
exception when others then -- Begin Log if error perform public.createlog(1,'ERROR: ' || SQLERRM || ' ' || SQLSTATE, 'createscope', 'IsDefault: ' || cast(vIsDefault as varchar) || ', vParameters: ' || vparameters || ', Description: ' || vdescription);
RAISE EXCEPTION '%', 'ERROR: ' || SQLERRM || ' ' || SQLSTATE || ' at createscope'; -- End Log if error
END; $$;

